package com.example.demo;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.view.velocity.VelocityConfigurer;import org.springframework.web.servlet.view.velocity.VelocityViewResolver;import java.util.Properties;@Configurationpublic class VelocityConfigure {    /**     * velocity 解析器配置     * @return     */    @Bean    VelocityViewResolver viewResolver(){        VelocityViewResolver resolver = new VelocityViewResolver();        resolver.setSuffix(".html");        resolver.setPrefix("/templates/");        resolver.setCache(false);        resolver.setExposeSessionAttributes(true);        resolver.setExposeRequestAttributes(true);        resolver.setContentType("text/html;charset=UTF-8");        return resolver;    }    /**     * velocity 引擎配置     * @return     */    @Bean    VelocityConfigurer velocityConfigurer(){        VelocityConfigurer velocityConfigurer = new VelocityConfigurer();        Properties properties = new Properties();        properties.setProperty("input.encoding","UTF-8");        properties.setProperty("output.encoding","UTF-8");        velocityConfigurer.setVelocityProperties(properties);        velocityConfigurer.setResourceLoaderPath("/templates/");        return velocityConfigurer;    }}